import React, { useState } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

function App() {
  const [keyword, setKeyword] = useState('');
  const [pincode, setPincode] = useState('');
  const [pages, setPages] = useState(1);
  const [data, setData] = useState([]);
  const [pagesScraped, setPagesScraped] = useState(0);
  const [totalProducts, setTotalProducts] = useState(0);
  const [loading, setLoading] = useState(false);
  const [filterText, setFilterText] = useState('');

  const handleScrape = async () => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/scrape', {
        search_keyword: keyword,
        pincode: pincode,
        pages: pages
      });
      setData(res.data.data);
      setPagesScraped(res.data.pages_scraped);
      setTotalProducts(res.data.total_products);
    } catch (err) {
      alert("Error fetching data.");
    }
    setLoading(false);
  };

  const handleDownload = async () => {
    try {
      const res = await axios.get('http://localhost:5000/download', { responseType: 'blob' });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'scraped_results.xlsx');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      alert("No data to download.");
    }
  };

  const filteredData = data.filter(item =>
    item.Title.toLowerCase().includes(filterText.toLowerCase())
  );

  const getRatingCounts = () => {
    const map = {};
    data.forEach(item => {
      const rating = item.Rating.split(' ')[0]; // e.g., '4.2 out of 5 stars'
      if (!isNaN(parseFloat(rating))) {
        const key = parseFloat(rating).toFixed(1);
        map[key] = (map[key] || 0) + 1;
      }
    });
    return Object.entries(map).map(([rating, count]) => ({ rating, count }));
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial' }}>
      <h1 style={{ color: '#0f62fe' }}>üõçÔ∏è Amazon Scraper Dashboard</h1>

      <div style={{ marginBottom: '1rem' }}>
        <input placeholder="üîç Search Keyword" value={keyword} onChange={e => setKeyword(e.target.value)} style={{ padding: '10px', marginRight: '10px' }} />
        <input placeholder="üìç Pincode" value={pincode} onChange={e => setPincode(e.target.value)} style={{ padding: '10px', marginRight: '10px' }} />
        <input type="number" min="1" value={pages} onChange={e => setPages(e.target.value)} style={{ padding: '10px', width: '80px', marginRight: '10px' }} />
        <button onClick={handleScrape} style={{ padding: '10px 20px', backgroundColor: '#0f62fe', color: 'white', border: 'none', borderRadius: '5px' }}>Scrape Now</button>
      </div>

      {loading && <p style={{ color: '#ff832b' }}>Scraping in progress...</p>}

      {!loading && totalProducts > 0 && (
        <>
          <p>‚úÖ Pages Scraped: <b>{pagesScraped}</b></p>
          <p>üõí Products Scraped: <b>{totalProducts}</b></p>

          <button onClick={handleDownload} style={{ marginBottom: '1rem', padding: '10px 20px', backgroundColor: '#24a148', color: 'white', border: 'none', borderRadius: '5px' }}>
            üì• Download Excel
          </button>

          <input
            placeholder="Filter by product title..."
            value={filterText}
            onChange={(e) => setFilterText(e.target.value)}
            style={{ padding: '8px', width: '300px', marginBottom: '1rem', display: 'block' }}
          />

          <h3>üìä Rating Distribution</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={getRatingCounts()}>
              <XAxis dataKey="rating" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="count" fill="#0f62fe" />
            </BarChart>
          </ResponsiveContainer>

          <table border="1" cellPadding="10" style={{ width: '100%', marginTop: '1rem', borderCollapse: 'collapse' }}>
            <thead style={{ backgroundColor: '#f4f4f4' }}>
              <tr>
                <th>Title</th>
                <th>Rating</th>
                <th>Discount Price</th>
                <th>Original Price</th>
                <th>Rating Count</th>
                <th>Availability</th>
              </tr>
            </thead>
            <tbody>
              {filteredData.map((item, i) => (
                <tr key={i}>
                  <td>{item.Title}</td>
                  <td>{item.Rating}</td>
                  <td>{item["Discount Price"]}</td>
                  <td>{item["Original Price"]}</td>
                  <td>{item["Rating Count"]}</td>
                  <td>{item.Availability}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default App;
