import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [keyword, setKeyword] = useState('');
  const [pincode, setPincode] = useState('');
  const [data, setData] = useState([]);
  const [pagesScraped, setPagesScraped] = useState(0);
  const [loading, setLoading] = useState(false);

  const handleScrape = async () => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/scrape', {
        search_keyword: keyword,
        pincode: pincode,
      });
      setData(res.data.data);
      setPagesScraped(res.data.pages_scraped);
    } catch (err) {
      alert("Error fetching data.");
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Amazon Product Dashboard</h1>
      <input
        placeholder="Search Keyword"
        value={keyword}
        onChange={(e) => setKeyword(e.target.value)}
      />
      <input
        placeholder="Pincode"
        value={pincode}
        onChange={(e) => setPincode(e.target.value)}
        style={{ marginLeft: '10px' }}
      />
      <button onClick={handleScrape} style={{ marginLeft: '10px' }}>Scrape</button>

      {loading && <p>Scraping in progress...</p>}

      {!loading && data.length > 0 && (
        <>
          <p>Pages Scraped: {pagesScraped}</p>
          <table border="1" cellPadding="10" style={{ marginTop: '2rem' }}>
            <thead>
              <tr>
                <th>Title</th>
                <th>Rating</th>
                <th>Discount Price</th>
                <th>Original Price</th>
                <th>Rating Count</th>
                <th>Availability</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, i) => (
                <tr key={i}>
                  <td>{item.Title}</td>
                  <td>{item.Rating}</td>
                  <td>{item["Discount Price"]}</td>
                  <td>{item["Original Price"]}</td>
                  <td>{item["Rating Count"]}</td>
                  <td>{item.Availability}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default App;
