import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [keyword, setKeyword] = useState('');
  const [pincode, setPincode] = useState('');
  const [pages, setPages] = useState(1);
  const [data, setData] = useState([]);
  const [pagesScraped, setPagesScraped] = useState(0);
  const [totalProducts, setTotalProducts] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);

  const handleScrape = async () => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/scrape', {
        search_keyword: keyword,
        pincode: pincode,
        pages: pages
      });
      setData(res.data.data);
      setPagesScraped(res.data.pages_scraped);
      setTotalProducts(res.data.total_products);
      setFileName(res.data.file_name);
    } catch (err) {
      alert("Error fetching data.");
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial' }}>
      <h1 style={{ color: '#0f62fe' }}>🛍️ Amazon Scraper Dashboard</h1>

      <div style={{ marginBottom: '1rem' }}>
        <input
          placeholder="🔍 Search Keyword"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
          style={{ padding: '10px', marginRight: '10px', borderRadius: '5px' }}
        />
        <input
          placeholder="📍 Pincode"
          value={pincode}
          onChange={(e) => setPincode(e.target.value)}
          style={{ padding: '10px', marginRight: '10px', borderRadius: '5px' }}
        />
        <input
          type="number"
          min="1"
          placeholder="📄 Pages"
          value={pages}
          onChange={(e) => setPages(e.target.value)}
          style={{ padding: '10px', width: '80px', marginRight: '10px', borderRadius: '5px' }}
        />
        <button
          onClick={handleScrape}
          style={{
            padding: '10px 20px',
            backgroundColor: '#0f62fe',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          Scrape Now
        </button>
      </div>

      {loading && <p style={{ color: '#ff832b' }}>⏳ Scraping in progress...</p>}

      {!loading && totalProducts > 0 && (
        <>
          <p>✅ Pages Scraped: <b>{pagesScraped}</b></p>
          <p>🛒 Products Scraped: <b>{totalProducts}</b></p>

          <a
            href={`http://localhost:5000/download/${fileName}`}
            download
            style={{
              display: 'inline-block',
              marginTop: '10px',
              padding: '10px 20px',
              backgroundColor: '#28a745',
              color: 'white',
              textDecoration: 'none',
              borderRadius: '5px'
            }}
          >
            ⬇️ Download Excel
          </a>

          <table border="1" cellPadding="10" style={{ width: '100%', marginTop: '1rem', borderCollapse: 'collapse' }}>
            <thead style={{ backgroundColor: '#f4f4f4' }}>
              <tr>
                <th>Title</th>
                <th>Rating</th>
                <th>Discount Price</th>
                <th>Original Price</th>
                <th>Rating Count</th>
                <th>Availability</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, i) => (
                <tr key={i}>
                  <td>{item.Title}</td>
                  <td>{item.Rating}</td>
                  <td>{item["Discount Price"]}</td>
                  <td>{item["Original Price"]}</td>
                  <td>{item["Rating Count"]}</td>
                  <td>{item.Availability}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default App;
