import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [keyword, setKeyword] = useState('');
  const [pincode, setPincode] = useState('');
  const [pages, setPages] = useState(1);
  const [data, setData] = useState([]);
  const [pagesScraped, setPagesScraped] = useState(0);
  const [totalProducts, setTotalProducts] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);

  const handleScrape = async () => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/scrape', {
        search_keyword: keyword,
        pincode: pincode,
        pages: pages
      });
      setData(res.data.data);
      setPagesScraped(res.data.pages_scraped);
      setTotalProducts(res.data.total_products);
      setFileName(res.data.file_name); // backend should return this
    } catch (err) {
      alert("Error fetching data.");
    }
    setLoading(false);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>üõçÔ∏è Amazon Product Cards</h1>

      <div style={styles.form}>
        <input
          placeholder="üîç Search Keyword"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
          style={styles.input}
        />
        <input
          placeholder="üìç Pincode"
          value={pincode}
          onChange={(e) => setPincode(e.target.value)}
          style={styles.input}
        />
        <input
          type="number"
          min="1"
          placeholder="üìÑ Pages"
          value={pages}
          onChange={(e) => setPages(e.target.value)}
          style={styles.input}
        />
        <button onClick={handleScrape} style={styles.scrapeButton}>üöÄ Scrape</button>
        {fileName && (
          <a
            href={`http://localhost:5000/download/${fileName}`}
            download
            style={styles.downloadButton}
          >
            ‚¨áÔ∏è Download Excel
          </a>
        )}
      </div>
a
      {loading && <p style={{ color: '#ff832b' }}>‚è≥ Scraping in progress...</p>}

      {!loading && totalProducts > 0 && (
        <>
          <p>‚úÖ Pages Scraped: <b>{pagesScraped}</b></p>
          <p>üõí Products Scraped: <b>{totalProducts}</b></p>

          <div style={styles.cardGrid}>
            {data.map((item, i) => (
              <div key={i} style={styles.card}>
                <img src={item.Image} alt="product" style={styles.image} />
                <div style={styles.cardBody}>
                  <h3 style={styles.title}>{item.Title.slice(0, 80)}...</h3>
                  <p><strong>Rating:</strong> {item.Rating}</p>
                  <p><strong>Discount:</strong> {item["Discount Price"]}</p>
                  <p><strong>Original:</strong> {item["Original Price"]}</p>
                  <p><strong>Availability:</strong> {item.Availability}</p>
                  <a href={item.ProductLink} target="_blank" rel="noreferrer">
                    <button style={styles.linkButton}>üîó View on Amazon</button>
                  </a>
                </div>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

const styles = {
  container: { padding: '2rem', fontFamily: 'Arial, sans-serif' },
  header: { color: '#0f62fe' },
  form: { marginBottom: '2rem', display: 'flex', gap: '1rem', flexWrap: 'wrap' },
  input: { padding: '10px', borderRadius: '5px', border: '1px solid #ccc' },
  scrapeButton: {
    padding: '10px 20px',
    backgroundColor: '#0f62fe',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  },
  downloadButton: {
    padding: '10px 20px',
    backgroundColor: '#24a148',
    color: 'white',
    textDecoration: 'none',
    borderRadius: '5px',
    display: 'inline-block'
  },
  cardGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '1rem',
    marginTop: '2rem'
  },
  card: {
    border: '1px solid #ccc',
    borderRadius: '10px',
    padding: '1rem',
    backgroundColor: '#fff',
    boxShadow: '0 0 10px rgba(0,0,0,0.05)'
  },
  image: { width: '100%', height: '200px', objectFit: 'contain' },
  cardBody: { marginTop: '10px' },
  title: { fontSize: '1rem', height: '48px', overflow: 'hidden' },
  linkButton: {
    marginTop: '10px',
    padding: '8px 12px',
    backgroundColor: '#ff8c00',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  }
};

export default App;
